module.exports.getBSPort = function(){

    if(process.env.STAGE ==='production')
    {
        return 8000;
    }
    else if(process.env.STAGE ==='development')
    {
        return 8000;
    }
    else {
        return  80;
    }

};

module.exports.getPhotoStorage = function(){

    var baseStorageLocation = "/static/photos"; //default value

    if(process.env.STAGE ==='production')
    {
        baseStorageLocation = "/static/photos";
    }
    else if(process.env.STAGE ==='development')
    {
        baseStorageLocation = "/static/photos";
    }
    else {
        baseStorageLocation = "/static/photos";
    }

    var photostorage  = {
        "originalStorage": baseStorageLocation + "/originalPhotos/",
        "inspectationStorage": {
            "initialPhotos":   baseStorageLocation + "/inspectationphotos/initialphotos/",
            "passedInspection":  baseStorageLocation + "/inspectationphotos/passedinspection/",
            "failedInspection":  baseStorageLocation + "/inspectationphotos/failedinspection/"
        },
        "correctionStorage": {
            "initialPhotos":   baseStorageLocation + "/correctionphotos/initialphotos/",
            "passedCorrection":  baseStorageLocation + "/correctionphotos/passedcorrection/",
            "failedCorrection":  baseStorageLocation + "/correctionphotos/failedcorrection/"
        },
        "processedStorage": {
            "initialPhotos":   baseStorageLocation + "/processedphotos/initialphotos/",
            "passedProcessed":  baseStorageLocation + "/processedphotos/passedprocessed/",
            "failedProcessed":  baseStorageLocation + "/processedphotos/failedprocessed/"
        }
    };


    return  photostorage;
};