var oMDM = require('../mdm_Interface.js');

exports.testSendEmailMessage = function(test) {
        var m = {op:'sendEmailMessage', pl:null};
        m.pl = {
        from: 'nodesmtp@hotmail.com',
        to: 'leo@lbsconsulting.com.cn',
        subject: 'Testing Sending Email10',
        body: 'This is a test of the mdm message sending capability..',
        attachments: null
    };

    oMDM.sendEmailMessage(m)
    .then(function(r) {
        //console.log("Send Mail Response: " + parammmInfo.response.toString());
        test.expect(2);
        test.equals(r.err, null);
        test.equals(r.pl, true);
        test.done();
    })
    .fail(function(r){
      console.log(r);
    });
};
