module.exports = function(paramMongoose) {
    var logSchema = paramMongoose.Schema({
        userAccountID: paramMongoose.Schema.Types.ObjectId, //UserAccountID
        opType: String,
        opName: String,
        opMethod: String,
        opDate: { type: Date, default: Date.now },
        srcIP: String,
        srcURI: String,
        detail: String,
        status: String
    });

    return logSchema;
};

