module.exports = function (paramMongoose){
  
var groupSchema = paramMongoose.Schema({
     _id: String,
    //createdOn: {type: Date, default: Date.now}, 
    groupName: String,
    description: String,
    memberCount: Number
});

groupSchema.statics.createGroup = function(paramGroupName, paramDescription, paramCallback){
this.groupName= paramGroupName;
this.description= paramDescription;
this.save(paramCallback);
};

return groupSchema;

};