module.exports = function (paramMongoose){

    var organizationSchema = paramMongoose.Schema({
        _id: String,
        //createdOn: {type: Date, default: Date.now},
        organizationName: String,
        description: String,
        memberCount: Number
    });

    organizationSchema .statics.createOrganization = function(paramOrganizationName, paramDescription, paramCallback){
        this.organizationName = paramOrganizationName;
        this.description= paramDescription;
        this.save(paramCallback);
    };

    return organizationSchema;

};