exports.version ="1.0.0";

var config = require('./config.js');
var mongoose = config.mongoose();

var EmailSchema = require("./models/Email.js")(mongoose);
var Email = mongoose.model('emails', EmailSchema); //exposes static methods
var oEmail = new Email(); //exposes instance methods

module.exports.sendEmailMessage = function(paramEmailMessage, paramCallback){
return oEmail.sendEmail(paramEmailMessage, paramCallback);    
};
