var oMDM = require('../mdm.js');

exports.testSendEmailMessage = function(test) {
     var paramEmailMessage = {
        from: 'nodesmtp@hotmail.com',
        to: 'leo@lbsconsulting.com.cn',
        subject: 'Testing Sending Email10',
        body: 'This is a test of the mdm message sending capability..',
        attachments: null
    };
    oMDM.sendEmailMessage(paramEmailMessage, function(paramError, paramSendStatus) {
        test.expect(2);
        test.equals(paramError, null);
        test.equals(paramSendStatus, true);
        test.done();
    });
};

