var oOLM = require('../olm.js');
var httpRequest = require('request');
var hostAddress = 'http://localhost';

exports.testReadOperationsLogs = function(test) {
    
    var paramUserAccountID = '5433c1a0e78641b2346c7508';;
    
    oOLM.readOperationsLogs(paramUserAccountID, '业务操作', null, null, function(paramError, paramLogsJson) {
        
        if(paramError){
            console.log("An error accured : " + paramError);
        }
        else {
            console.log('Json Return ' + paramLogsJson );
        }
        //function(paramUserAccountID,paramOpType, paramPageNumber, paramPageSize, paramCallback)
        //console.log(paramLogsJson[0].toString());
        test.expect(3);
        test.equals(null, null);
        test.equals(paramUserAccountID, paramLogsJson[0].userAccountID);
        test.equals(true, true);
        test.done();
    });
};

exports.testlogRequest = function(test) {
    httpRequest.get(hostAddress + '/home/registration', function(paramError, paramResponse, paramBody) {
        console.log(paramBody.toString());
        test.expect(2);
        test.equals(paramError, null);
        test.equals(paramResponse.statusCode, 200);
        test.done();
    });
};

