exports.version ="1.0.0";

var config = require('./config.js');
var mongoose = config.mongoose();

 
var PersonalSchema = require("./models/Personal.js")(mongoose);
var OrganizationSchema = require("./models/Organization.js")(mongoose);

var Personal = mongoose.model('personals', PersonalSchema); //exposes static methods
var oPersonal = new Personal(); //exposes instance methods


module.exports.createPersonalProfile = function(paramPersonalProfileJson, paramCallback){
Personal.create(paramPersonalProfileJson, paramCallback);    
};

module.exports.updatePersonalProfile = function(paramUserAccountID, paramPersonalProfileJson, paramCallback){
Personal.updatePersonalProfile(paramUserAccountID, paramPersonalProfileJson, paramCallback);    
};

module.exports.readPersonalProfile = function(paramUserAccountID, paramCallback){
Personal.readByUserAccountID(paramUserAccountID, paramCallback);    
};
